document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector("header"),t=document.querySelector(".hamburguer"),o=document.querySelector(".nav-ul"),c=document.querySelector(".block-container");window.addEventListener("scroll",()=>{e=document.querySelector("header"),e.classList.toggle("sticky",0<window.scrollY)}),t.addEventListener("click",()=>{o.classList.toggle("show")}),c.addEventListener("click",()=>{o.classList.contains("show")&&o.classList.toggle("show")})});